import React,{useState, useEffect} from 'react';
import axios from 'axios';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";

import { Card, Button } from 'react-bootstrap';

const ItemDetail = ({match}) => {
    console.log(match)
    let itemID = match.params.id;
    console.log(itemID)

    const [item, setItem] = useState([]);

    useEffect(() => {
        axios(`./itemList.json/${itemID}`)
        .then((res) => setItem(res.data))
    }, [itemID]);

    return (
        <div>
            {item.map((data)=> {
                <Card style={{ width: '18rem' }} className='item-context' key={data.id}>
                        <Card.Img variant="top" src= {img} />
                        <Card.Body className="item-body">
                            <Card.Title> {data.nombre} </Card.Title>
                            <Card.Text> {descripcion} </Card.Text>
                            <Card.Text> Precio: $ {precio} </Card.Text>
                            <div className="buttons-container">
                                <div className="buttons-block">
                                    <Button className='item-button sumar' onClick={sumar} > + </Button>
                                    <Card.Text style={{ marginBottom: '0', fontSize: '20px', padding: '0 0.5rem' }} className="cantidad-num"> {cantidad} </Card.Text>
                                    <Button className='item-button restar' onClick={restar} > - </Button>
                                </div>
                                <Button className='item-button'> Comprar </Button>
                            </div>
                        </Card.Body>
                </Card>
            })}

        </div>
    )
}

export default ItemDetail
