import React from 'react';
import './../ItemDetail/ItemDetail.css';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Button } from 'react-bootstrap';


const ItemDetail = ({id, img, nombre, descripcion, precio}) => {
    return (
        <div className="item-container">
            <img className="item-img" src={img} alt={nombre} />
            <div classname="itemBody-detail">
                <h1>{nombre}</h1>
                <p>{descripcion}</p>

            </div>
            <Card style={{ width: '18rem' }} className='item-context' key={id}>
                <Card.Img variant="top" src= {img} />
                <Card.Body className="item-body">
                    <Card.Title> {nombre} </Card.Title>
                    <Card.Text> {descripcion} </Card.Text>
                    <Card.Text> Precio: $ {precio} </Card.Text>
                    <Button style={{ backgroundColor:'#c59534', outline:'none', border:'transparent', borderRadius:'3px'}} className='item-button'> Comprar </Button>
                </Card.Body>
            </Card>
        </div>
    )
}

export default ItemDetail;
