import React from 'react';
import logo from "./comarca-logo-ok.png"
import "./NavBar.css";

//IMPORT LINKS
import { Link, useHistory } from 'react-router-dom';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Nav, Button, NavDropdown } from 'react-bootstrap';



const NavBar = ({item1, item2, item3, subitem1, subitem2, subitem3, subitem4}) => {
    const history  = useHistory();

    const handleChange = (el) => {
        if(el.target.value)
            history.push(`/categoria/${el.target.value}`)
    };

    return (
        <Navbar expand="lg" className="nav-container">
            <Navbar.Brand><Link to="/" className="Link"><img width="200" className="d-inline-block align-top" src={logo} alt="comarca de creadores logo" /></Link></Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                    {/* <p>{item1}</p>
                    <select onChange={handleChange}>
                        <option value="Artística">{subitem1} </option>
                        <option value="Librería">{subitem2} </option>
                        <option value="Mercería">{subitem3} </option>
                        <option value="Emprendedores">{subitem4} </option>
                    </select> */}
                    <NavDropdown title= {item1} id="basic-nav-dropdown" onChange={handleChange}>
                        <NavDropdown.Item><Link to="/tienda" className="Link">{subitem1}</Link></NavDropdown.Item>
                        <NavDropdown.Item><Link to="/tienda" className="Link">{subitem2}</Link></NavDropdown.Item>
                        <NavDropdown.Item><Link to="/tienda" className="Link">{subitem3}</Link></NavDropdown.Item>
                        <NavDropdown.Item><Link to="/tienda" className="Link">{subitem4}</Link></NavDropdown.Item>
                    </NavDropdown>
                    <Link to="/talleres" className="Link"> {item2} </Link>
                    <Link to="/nosotros" className="Link"> {item3} </Link>
                </Nav>
                <Button id="cart-button" className="cart-button" style={{backgroundColor:"#c59534", outline:"0", border: "none"}}><i className="fas fa-shopping-cart"></i></Button> 
            </Navbar.Collapse>
        </Navbar>
    );
};

export default NavBar;
