    const [cantidad, setCantidad] = useState(0)

    const sumar = () => {
        if(cantidad < 5){
            setCantidad(cantidad + 1);
        };
    };

    const restar = () => {
        if(cantidad > 0) {
        setCantidad(cantidad - 1);
        };
    };

    const cartLoad = () => {
        console.log()
    };


    return (
        <div className='buttons-container'>
            <div className='buttons-block'>
                <button className='item-button sumar' onClick={sumar} > + </button>
                <p className='cantidad-num'> {cantidad} </p>
                <button className='item-button restar' onClick={restar} > - </button>
            </div>
            <button className='item-button añadirCart-btn'onClick={cartLoad} > Añadir al Carrito </button>
        </div>
    )
}

export default ItemCount;
