import React, { useState } from "react";
import "../CheckoutForm/CheckoutForm.css";

import { useCartContext } from "../../Contexts/CartContext";

const CheckoutForm = ({ addOrden }) => {
  const { cart, reduceCart } = useCartContext();

  const initialState = {
    nombre: "",
    telefono: "",
    email: "",
    productos: "",
  };

  const [values, setValues] = useState(initialState);

  const handleOnSubmit = (event) => {
    event.preventDefault();
    console.log(values);
    addOrden(values);
    setValues({ ...initialState });
  };

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    console.log(name, value);
    setValues({ ...values, [name]: value });
  };

  return (
    <div className='checkout-container'>
      <div className='checkout-titles'>
        <h2>A un click de tu compra</h2>
        <p>Completá los datos solicitados para confirmar tu pedido</p>
      </div>
      <form className='form-container' onSubmit={handleOnSubmit}>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='Nombre y Apellido'
            onChange={handleOnChange}
            name='nombre'
            value={values.nombre}
          />
        </div>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='Teléfono'
            onChange={handleOnChange}
            name='telefono'
            value={values.telefono}
          />
        </div>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='E mail'
            onChange={handleOnChange}
            name='email'
            value={values.email}
          />
        </div>
        <div>
        {cart.map((item) => (
          <div className="cart-container" key={item.id}>
            <h4>Productos</h4>
            <p className="item-description">{item.nombre} </p>
            <p className="item-description">({item.quantity}) unidades</p>
          </div>
      ))}
      <h3 className="total-text">Total: ${reduceCart} </h3>
        </div>
        <h4>Total a pagar: ${reduceCart} </h4>
        <button type='submit' className='form-button'>
          Enviar Orden
        </button>
      </form>
    </div>
  );
};

export default CheckoutForm;
