import React, { createContext, useContext } from "react";

export const OrdersContext = createContext({});
export const useOrdersContext = () => useContext(OrdersContext);

import { db } from "../firebase";
import firebase from "firebase/app";
import { useCartContext } from "../Contexts/CartContext";

let keyId;
let buyerInfo;
const OrdersProvider = ({ children }) => {
  const { cart, clearCart, reduceCart } = useCartContext();

  let paso = 1;
  const [pasos, setPasos] = useState(paso);

  const clear = () => clearCart();
  console.log(cart);
  const itemsCart = cart.map((cartItem) => {
    return {
      id: cartItem.id,
      nombre: cartItem.nombre,
      precio: cartItem.precio,
      quantity: cartItem.quantity,
    };
  });

  const addOrdenInfo = (values) => {
    buyerInfo = values;
    setPasos(2);
  };

  const addOrden = () => {
    db.collection("ordenes")
      .add({
        buyer: buyerInfo,
        items: itemsCart,
        date: firebase.firestore.FieldValue.serverTimestamp(),
        total: reduceCart,
      })
      .then((docRef) => {
        keyId = docRef.id;
        console.log(keyId);
        updateStock();
        clear();
        setPasos(3);
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  const updateStock = () => {
    itemsCart.forEach(async (elem) => {
      const product = await db.collection("productos").doc(elem.id).get();
      const ProdStock = product.data().stock - elem.quantity;
      await db.collection("productos").doc(elem.id).update({
        stock: ProdStock,
      });
    });
  };

  return (
    <OrdersContext.Provider value={{ addOrden, keyId, addOrdenInfo }}>
      {children}
    </OrdersContext.Provider>
  );
};

export default OrdersProvider;
