/* eslint-disable max-len */
import React /* , { useState, useEffect }  */ from "react";
import "./App.css";

import CartProvider from "./Contexts/CartContext";
import OrdersProvider from "./Contexts/OrdersContext";

import ItemListContainer from "./Components/ItemListContainer/ItemListContainer";
import ItemDetailContainer from "./Components/ItemDetailContainer/ItemDetailContainer";

/* import { db } from './firebase'; */

// REACT ROUTER DOM
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

// COMPONENTS
import NavBar from "./Components/NavBar/Navbar";
import Footer from "./Components/Footer/Footer";

// VISTAS
import Home from "./views/Home/Home";
import Talleres from "./views/Talleres/Talleres";
import Nosotros from "./views/Nosotros/Nosotros";
import Cart from "./Components/Cart/Cart";
import Orders from "./Components/CheckoutForm/Orders";
import CheckOutConfirm from "./Components/CheckoutForm/CheckOutConfirm";
import CheckOutId from "./Components/CheckoutForm/CheckOutId";

const App = () => {
  return (
    <OrdersProvider>
      <CartProvider>
        <Router>
          <div className='main-container'>
            <NavBar
              item1={"Categoría:"}
              item2={"Talleres"}
              item3={"Nosotros"}
              subitem0={"Todos"}
              subitem1={"Artística"}
              subitem2={"Librería"}
              subitem3={"Mercería"}
              subitem4={"Emprendedores"}
            />
          </div>

          <Switch>
            <Route path='/' exact component={Home} />
            <Route path='/categoria/:categoryName'>
              <ItemListContainer />
            </Route>
            <Route path='/talleres' component={Talleres} />
            <Route path='/nosotros' component={Nosotros} />
            <Route path='/item/:id'>
              <ItemDetailContainer />
            </Route>
            <Route path='/cart'>
              <Cart />
            </Route>
            <Route path='/checkout'>
              <Orders />
            </Route>
            <Route path='/confirmacion'>
              <CheckOutConfirm />
            </Route>
            <Route path='/orden'>
              <CheckOutId />
            </Route>
            <Route path='*'>
              <h1>404 - Page not found</h1>
            </Route>
          </Switch>

          <div>
            <Footer />
          </div>
        </Router>
      </CartProvider>
    </OrdersProvider>
  );
};

export default App;
