import "../CheckoutForm/CheckoutForm.css";

import {useCartContext} from '../../Contexts/CartContext';

const CheckoutForm = () => {

    const {cart, reduceCart} = useCartContext();

  return (
    <div>
      <div>
        <h2>A un click de tu compra</h2>
        <p>Completá los datos solicitados para confirmar tu pedido</p>
      </div>
      <form>
        <div>
          <input
            type='text'
            placeholder='Nombre y Apellido'
            name='nombre'
            value=''
          />
        </div>
        <div>
          <input type='text' placeholder='Teléfono' name='telefono' value='' />
        </div>
        <div>
          <input type='text' placeholder='E mail' name='email' value='' />
        </div>
        <div>
            <p>Total a pagar: ${reduceCart} </p>
        </div>
        <button type='submit'>Enviar Orden</button>
      </form>
    </div>
  );
};

export default CheckoutForm;
