import './../Item/Item.css';
import ItemCount from "./../ItemCount/ItemCount";

import { Link } from 'react-router-dom';
import { useCartContext } from '../../Contexts/CartContext';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";
import { Card } from 'react-bootstrap';


const Item = ({id, img, nombre, precio, stock}) => {

    return (
        <div>
            <Card style={{ width: '18rem' }} className='item-context'>
                <Link to={`/item/${id}`}>
                    <Card.Img variant="top" src= {img} alt={nombre} />
                </Link>
                <Card.Body className="item-body">
                    <Card.Title> {nombre} </Card.Title>
                    <Card.Text> Precio: $ {precio} </Card.Text>
                    <div className="container-btnsDetail">
                    {stock > 0 && <ItemCount onAdd={onAdd} stock={product.stock} /> }
                    {product.stock <= 0 && <p className="noStock">SIN STOCK</p> }
                </div>
                </Card.Body>
            </Card>
        </div>
    )
}

export default Item;
