import React, {useState} from 'react';
import './Item.css';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";

import { Card, Button } from 'react-bootstrap';

function Item({img, nombre, descripcion, precio} ) {
    const [cantidad, setCantidad] = useState(0)

    const sumar = () => {
        if(cantidad < 5){
            setCantidad(cantidad + 1);
        };
      };

      const restar = () => {
          if(cantidad > 0) {
            setCantidad(cantidad - 1);
          };
      };

    return (
        <Card style={{ width: '18rem' }} className='item-context'>
                <Card.Img variant="top" src= {img} />
                <Card.Body className="item-body">
                    <Card.Title> {nombre} </Card.Title>
                    <Card.Text> {descripcion} </Card.Text>
                    <Card.Text> Precio: $ {precio} </Card.Text>

                    <div className="buttons-block">
                        <Button className='item-button sumar' onClick={sumar} > + </Button>
                        <Card.Text style={{ marginBottom: '0', fontSize: '20px', padding: '0 0.5rem' }} className="cantidad-num"> {cantidad} </Card.Text>
                        <Button className='item-button restar' onClick={restar} > - </Button>
                    </div>
                    <Button className='item-button'> Comprar </Button>
                </Card.Body>
        </Card>
    )
}

export default Item;
