import React from 'react';
import ItemCount from './../../Components/ItemCount/ItemCount';
import './../ItemDetail/ItemDetail.css';

import { useCartContext } from './../../Contexts/CartContext';
import { Link } from 'react-router-dom';

const ItemDetail = ({item}) => {

    const { cart, addToCart } = useCartContext();

    const onAdd = (qty) => addToCart(item, qty);
    //console.log(cart)

    return (
        <div className="item-container" key={item.id}>
            <img className="item-img" src={item.img} alt={item.nombre} />
            <div className="itemBody-detail">
                <h1 className="itemTitle">{item.nombre}</h1>
                <p>{item.descripcion}</p>
                <p><strong>Precio: ${item.precio}</strong></p>
                <div className="container-btnsDetail">
                    {item.stock > 0 && <ItemCount onAdd={onAdd} stock={item.stock} /> }
                    {item.stock <= 0 && <p className="noStock">SIN STOCK</p> }
                    <Link to="/cart">
                        {cart.length !== 0 && <button className="detail-buy" >Finalizar Compra</button>}
                    </Link>
                </div>
            </div>
        </div>
    )
}

export default ItemDetail;
