      querySnapshot.forEach((doc) => {
        // console.log(doc.data());
        console.log(doc.id);
        ordenId.push({ id: doc.id });
      });
      setOrden(ordenId);
    });
  };

  useEffect(() => {
    ge();
  }, []);

  return (
    <div>
      <CheckoutForm addOrden={addOrden} orden={orden} />
