/* import React from "react";
import CheckoutForm from "./CheckoutForm";

import { db } from "../../firebase";
import firebase from "firebase/app";
import { useCartContext } from "../../Contexts/CartContext";

let keyId;
let buyerInfo;

const Orders = () => {
  const { cart, clearCart, reduceCart } = useCartContext();

  const clear = () => clearCart();

  console.log(cart);
  const itemsCart = cart.map((cartItem) => {
    return {
      id: cartItem.id,
      nombre: cartItem.nombre,
      precio: cartItem.precio,
      quantity: cartItem.quantity,
    };
  });

  const addOrdenInfo = (compra) => {
    buyerInfo = compra;
  };

  const addOrden = () => {
    db.collection("ordenes")
      .add({
        buyer: buyerInfo,
        items: itemsCart,
        date: firebase.firestore.FieldValue.serverTimestamp(),
        total: reduceCart,
      })
      .then((docRef) => {
        keyId = docRef.id;
        updateStock();
        clear();
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  const updateStock = () => {
    itemsCart.forEach(async (elem) => {
      const product = await db.collection("productos").doc(elem.id).get();
      const ProdStock = product.data().stock - elem.quantity;
      await db.collection("productos").doc(elem.id).update({
        stock: ProdStock,
      });
    });
  };

  return (
    <div>
      <CheckoutForm
        addOrden={addOrden}
        keyId={keyId}
        addOrdenInfo={addOrdenInfo}
      />
    </div>
  );
};

export default Orders; */



export default Orders;
