import React, { useState } from "react";
import "../CheckoutForm/CheckoutForm.css";

import { Link } from "react-router-dom";
import { useCartContext } from "../../Contexts/CartContext";

const CheckoutForm = ({ addOrden }) => {
  const { cart, reduceCart } = useCartContext();

  const initialState = {
    nombre: "",
    telefono: "",
    email: "",
    productos: [{ producto: "", cantidad: "" }],
    total: "",
  };

  const [values, setValues] = useState(initialState);

  const handleOnSubmit = (event) => {
    event.preventDefault();
    console.log(values);
    addOrden(values);
    setValues({ ...initialState })
    SpeechRecognitionAlternative();
  };

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    console.log(name, value);
    setValues({ ...values, [name]: value });
  };

  return (
    <div className='checkout-container'>
      <div className='checkout-titles'>
        <h2>A un click de tu compra</h2>
        <p>Completá los datos solicitados para confirmar tu pedido</p>
      </div>
      <form className='form-container' onSubmit={handleOnSubmit}>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='Nombre y Apellido'
            onChange={handleOnChange}
            name='nombre'
            value={values.nombre}
          />
        </div>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='Teléfono'
            onChange={handleOnChange}
            name='telefono'
            value={values.telefono}
          />
        </div>
        <div>
          <input
            className='input-field'
            type='text'
            placeholder='E mail'
            onChange={handleOnChange}
            name='email'
            value={values.email}
          />
        </div>
        <div>
          <h4>Resumen de compra</h4>
          {cart.map((item) => (
            <div className='products-container' key={item.id}>
              <p className='product-name'>{item.nombre} </p>
              <p className='product-quantity'>({item.quantity}) unidades</p>
            </div>
          ))}
        </div>
        <h4>Total a pagar: ${reduceCart} </h4>
        <Link to='/thanks'>
          <button type='submit' className='form-button'>
            Enviar Orden
          </button>
        </Link>
      </form>
    </div>
  );
};

export default CheckoutForm;
