import React, {useState} from 'react';
import "./Cards.css";



//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";

import { Card, Button } from 'react-bootstrap';

function CardComponent({img, title, description}) {
    const [cantidad, setCantidad] = useState(0)

    const sumar = () => {
        setCantidad(cantidad + 1);
      };

      const restar = () => {
        setCantidad(cantidad - 1);
      };

    return (
        <Card style={{ width: '18rem' }} className='card-context'>
                <Card.Img variant="top" src= {img} />
                <Card.Body>
                    <Card.Title> {title} </Card.Title>
                    <Card.Text> {description} </Card.Text>
                    <div className="buttons-block">
                        <Button className='card-button sumar' onClick={sumar} > + </Button>
                        <Card.Text classname=""> {cantidad} </Card.Text>
                        <Button className='card-button restar' onClick={restar} > - </Button>
                    </div>
                    <Button className='card-button'> Comprar </Button>
                </Card.Body>
        </Card>

    );
};

export default CardComponent;
