import React,{useState, useEffect} from 'react';
import axios from 'axios';

//BOOTSTRAP
import "bootstrap/dist/css/bootstrap.min.css";

import { Card, Button } from 'react-bootstrap';

const ItemDetail = ({match}) => {
    console.log(match)
    let itemID = match.params.id;
    console.log(itemID)

    const [item, setItem] = useState([]);

    useEffect(() => {
        axios(`./itemList.json/${itemID}`)
        .then((res) => setItem(res.data))
    }, [itemID]);

    return (
        <div>
            {item.map((data)=> {
                return
                <Card style={{ width: '18rem' }} className='item-context' key={data.id}>
                        <Card.Img variant="top" src= {data.img} />
                        <Card.Body className="item-body">
                            <Card.Title> {data.nombre} </Card.Title>
                            <Card.Text> {data.descripcion} </Card.Text>
                            <Card.Text> Precio: $ {data.precio} </Card.Text>
                            <Button className='item-button'> Comprar </Button>
                        </Card.Body>
                </Card>
            })}

        </div>
    )
}

export default ItemDetail
