import React, {useState, useEffect} from 'react';
import ItemList from '../ItemList/ItemList';

/* import { db } from './../../firebase'; */

/* import axios from 'axios'; */

import { useParams } from 'react-router-dom';


function ItemListContainer() {
    const [items, setItems] = useState([]);

    const { categoryName } = useParams();

    /* useEffect(() => {
        (async() => {
            const { data } = await axios.get("https://60d24fa0858b410017b2d8d9.mockapi.io/api/v1/productos")
            if(!categoryName) return setItems(data);
            if(categoryName === "all") return setItems(data);
            const catItems = data.filter(item => item.categoria === categoryName);
            setItems(catItems)
        })();
    }, [categoryName]); */

    const getProductos = () => {
        const docs = []
        db.collection('productos').onSnapshot((querySnapshot) => {
          querySnapshot.forEach(doc => {
            docs.push({...doc.data(), id: doc.id })
          });
          if(!categoryName) return setItems(docs);
            if(categoryName === "all") return setItems(docs);
            const catItems = docs.filter(item => item.categoria === categoryName);
            setItems(catItems)
        });
      }

      useEffect(() => {
        getProductos();
      }, [categoryName]);

    return (
        <div>
                <ItemList items={items} />
        </div>
    );
};

export default ItemListContainer;
