import React, { useState, useEffect } from "react";
import CheckoutForm from "./CheckoutForm";

import { db } from "../../firebase";
import { CartContext } from "../../Contexts/CartContext";

let keyId;
let buyerInfo;

const Orders = () => {
  const { cart, clearCart, reduceCart } = useContext(CartContext);

  const clear = () => clearCart();

  const itemsCart = cart.map((el) => {
    return {
      id: el.id,
      title: el.title,
      price: el.price,
      quantity: el.quantity,
    };
  });

  const addOrdenInfo = (compra) => {
    buyerInfo = compra;
  };

  const addOrden = () => {
    db.collection("ordenes")
      .add({
        buyer: buyerInfo,
        items: itemsCart,
        date: firebase.firestore.FieldValue.serverTimestamp(),
        total: reduceCart,
      })
      .then((docRef) => {
        keyId = docRef.id;
        updateStock();
        clear();
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  };

  const updateStock = () => {
    itemsCart.forEach(async (elem) => {
      const product = await db.collection("productos").doc(elem.id).get();
      const ProdStock = product.data().stock - elem.quantity;
      await db.collection("productos").doc(elem.id).update({
        stock: ProdStock,
      });
    });
  };

  return (
    <div>
      <CheckoutForm addOrden={addOrden} keyId={keyId}  />
    </div>
  );
};

export default Orders;
